def part1():
    floor = 0
    for dir in INPUT:
        if dir == "(":
            floor += 1
        elif dir == ")":
            floor -= 1
    print(floor)


def part2():
    floor = 0
    pos = 0
    for dir in INPUT:
        pos += 1
        if dir == "(":
            floor += 1
        elif dir == ")":
            floor -= 1
        if floor == -1:
            break
    print(pos)


